	if (images.length === 1) {
		return (
			<>
				<img
					role="presentation"
					src={images[0].src} alt={images[0].src}/>
			</>
		)
	}

	return (
		<div>
			구현중...
		</div>
	);
};

PostImages.propTypes = {
	images: PropTypes.arrayOf(PropTypes.object),
