import { all, takeLatest, delay, put, fork, throttle, call } from 'redux-saga/effects';
import axios from 'axios';
import {
	ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE,
	ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE,
	REMOVE_POST_REQUEST, REMOVE_POST_SUCCESS, REMOVE_POST_FAILURE,
	LOAD_POSTS_REQUEST, LOAD_POSTS_SUCCESS, LOAD_POSTS_FAILURE,
	generateDummyPost, LIKE_POST_REQUEST, UNLIKE_POST_REQUEST, LIKE_POST_SUCCESS, UNLIKE_POST_SUCCESS,
} from '../reducers/post';
import { ADD_POST_TO_ME, REMOVE_POST_OF_ME } from '../reducers/user';

function likePostAPI() {
	return axios.get('/posts');
};

function* likePost(action) {
	try {
		const result = yield call(likePostAPI, action.data);
		yield put({
			type: LIKE_POST_SUCCESS,
			data: result.data,
		});
	} catch (err) {
		yield put({
			type: UNLIKE_POST_SUCCESS,
			error: err.response.data,
		});
	}
};

function unLikePostAPI() {
	return axios.get('/posts');
};

function* unLikePost(action) {
	try {
		const result = yield call(unLikePostAPI, action.data);
		yield put({
			type: LOAD_POSTS_SUCCESS,
			data: result.data,
		});
	} catch (err) {
		yield put({
			type: LOAD_POSTS_FAILURE,
			error: err.response.data,
		});
	}
};

function loadPostsAPI() {
	return axios.get('/posts');
};

function* loadPosts(action) {
	try {
		const result = yield call(loadPostsAPI, action.data);
		yield put({
			type: LOAD_POSTS_SUCCESS,
			data: result.data,
		});
	} catch (err) {
		yield put({
			type: LOAD_POSTS_FAILURE,
			error: err.response.data,
		});
	}
};

function addPostAPI(data) {
	return axios.post('/post', { content: data });
};

function* addPost(action) {
	try {
		const result = yield call(addPostAPI, action.data);
		yield put({
			type: ADD_POST_SUCCESS,
			data: result.data,
		});
		yield put({
			type: ADD_POST_TO_ME,
			data: result.data.id,
		})
	} catch (err) {
		yield put({
			type: ADD_POST_FAILURE,
			error: err.response.data,
		});
	}
};

function removePostAPI(data) {
	return axios.delete('/api/post', data);
};

function* removePost(action) {
	try {
		// const result = yield call(addPostAPI, action.data);
		yield delay(1000);
		yield put({
			type: REMOVE_POST_SUCCESS,
			data: action.data,
		});
		yield put({
			type: REMOVE_POST_OF_ME,
			data: action.data,
		})
	} catch (err) {
		yield put({
			type: REMOVE_POST_FAILURE,
			error: err.response.data,
		});
	}
};

function addCommentAPI(data) {
	return axios.post(`/post/${data.postId}/comment`, data);
};

function* addComment(action) {
	try {
		const result = yield call(addCommentAPI, action.data);
		yield put({
			type: ADD_COMMENT_SUCCESS,
			data: result.data,
		});
	} catch (err) {
		console.error(err);
		yield put({
			type: ADD_COMMENT_FAILURE,
			error: err.response.data,
		});
	}
};

function* watchLikePost() {
	yield throttle(5000, LIKE_POST_REQUEST, likePost);
};

function* watchUnLikePost() {
	yield throttle(5000, UNLIKE_POST_REQUEST, unLikePost);
};

function* watchLoadPosts() {
	yield throttle(5000, LOAD_POSTS_REQUEST, loadPosts);
};

function* watchAddPost() {
	yield takeLatest(ADD_POST_REQUEST, addPost);
};

function* watchRemovePost() {
	yield takeLatest(REMOVE_POST_REQUEST, removePost);
};

function* watchAddComment() {
	yield takeLatest(ADD_COMMENT_REQUEST, addComment);
};

export default function* postSaga() {
	yield all([
		fork(watchLikePost),
		fork(watchUnLikePost),
		fork(watchLoadPosts),
		fork(watchAddPost),
		fork(watchRemovePost),
		fork(watchAddComment),
	]);
};
